# frozen_string_literal: true

require "test_helper"

class TheFreeDictionaryGermanAudioTest < ActiveSupport::TestCase
  test "should get empty pronunciation" do
    statement = "0120324"
    http_module = Class.new do
      def self.get_response(url)
        Class.new do
          def self.body
            ""
          end
        end
      end
    end
    result = TheFreeDictionary::Pronunciation.get do |config|
      config.statement = statement
      config.http_module = http_module
      config.hostname = "de.thefreedictionary.com"
      config.language = "de"
      config.region = "DE"
    end
    assert_equal "", result[:source]
    assert_not result[:transcription]
  end

